'use strict';

// Switch case statements
let value = 10;

switch(value) {
    case 5:
    console.log("Value is 5");
    break;
    case 10:
    console.log("Value is 10");
    break;
    case 15:
    console.log("Value is 15");
    break;
    case 20:
    console.log("Value is 20");
    break;
    default:
    console.log("This case is the fallback - if no prevuius case matches");
}